mapper.select:

SELECT  (SELECT COUNT(*) 
                FROM SEAT
                WHERE ART_NO=${artNo}
                AND TIME_NO =${timeNo}
                AND STATUS='Y') AS 남은좌석  , COUNT(*) AS 총좌석
FROM SEAT
WHERE ART_NO=${artNo}
AND TIME_NO=${timeNo}
GROUP BY ART_NO

SELECT COUNT(*)
FROM SEAT
WHERE ART_NO=#{artNo}
AND TIME_NO=#{timeNo}

SELECT COUNT(*)
FROM SEAT
WHERE ART_NO=#{artNo}
AND TIME_NO =#{timeNo}
AND STATUS='Y'

SELECT *
FROM SEAT
WHERE TIME_NO =#{timeNo}
AND ART_NO=#{artNo}
ORDER BY 1

mapper.delete:
DELETE FROM PERSONAL
WHERE ART_NO = #{artNo} 
AND M_NO=#{alarmNo}

mapper.select:
SELECT COUNT(*)
FROM PERSONAL
WHERE ART_NO =#{artNo} AND M_NO=#{alarmNo}

mapper.update:
UPDATE ART
SET STATUS = 'N'
WHERE ART_NO = #{artNo}

UPDATE IMG SET ORIGIN_NAME=#{originName}, 
CHANGE_NAME=#{changeName},
FILE_PATH=#{filePath},
CHANGE_DATE=SYSDATE,
FILE_LEVEL=#{fileLevel}
WHERE ART_NO = #{artNo} AND FILE_LEVEL = #{fileLevel}

UPDATE TICKET_DATE SET TICKETING_DATE = #{ticketingDate} ,
START_DATE=#{startDate} ,
END_DATE=#{endDate}
WHERE ART_NO = #{artNo}
