mapper.select:
	SELECT *  FROM INTEREST_VIEW
	WHERE M_ID=#{id} 
    AND ROWNUM <=3

SELECT COUNT(*) FROM PURCHASE P JOIN ART a on(p.ART_NO=a.ART_NO) JOIN ART_TIME at on(p.TIME_NO=at.DATE_COUNT) JOIN MEMBER m on(m.M_NO=p.M_NO) WHERE TO_CHAR(DATE_TIME,'YYYYMMDDHH24MISS') < TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') AND m.M_ID=#{id} and p.ART_NO=at.ART_NO
AND ART_TITLE LIKE '%' || #{pName} || '%'
AND ART_TYPE = #{artType}
and TO_CHAR(DATE_TIME,'YYYY-MM-DD') BETWEEN #{sd} and #{ed}

SELECT DISTINCT p.purchase_no, a.art_title,at.date_time, p.purchase_count,p.art_no,a.address,p.seat_name,p.type, i.change_name,a.art_type, p.seat_sal ,(SELECT R_NO FROM REPLY C JOIN MEMBER M on(C.RWRITER=M.M_ID) where m.M_ID= #{id} AND C.STATUS='Y' AND C.art_no=P.art_no) as R_NO, (SELECT rcontent FROM REPLY C JOIN MEMBER M on(C.RWRITER=M.M_ID) where m.M_ID= #{id} AND C.STATUS='Y' AND C.art_no=P.art_no) as rcontent FROM PURCHASE P JOIN ART a on(p.ART_NO=a.ART_NO) JOIN ART_TIME at on(p.TIME_NO=at.DATE_COUNT) left JOIN IMG i on(p.ART_NO=i.ART_NO) JOIN MEMBER m on(m.M_NO=p.M_NO) WHERE TO_CHAR(DATE_TIME,'YYYYMMDDHH24MISS') < TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') AND m.M_ID=#{id} AND FILE_LEVEL = 0 and p.ART_NO=at.ART_NO
AND ART_TITLE LIKE '%' || #{pName} || '%'
AND ART_TYPE = #{artType}
and TO_CHAR(DATE_TIME,'YYYY-MM-DD') BETWEEN #{sd} and #{ed}
ORDER BY at.date_time ASC

SELECT TO_CHAR(ENROLL_DATE,'YYYY-MM-DD')
FROM MEMBER
WHERE M_ID=#{id}

SELECT *
FROM SEAT
WHERE TIME_NO =#{timeNo}
AND ART_NO=#{artNo}
ORDER BY 1

mapper.update:
UPDATE MEMBER
SET M_PWD=#{pwd}, M_NAME=#{name}

,GENDER=#{gender}


,BIRTH=#{birth}


,PHONE=#{phone}


,ADDRESS=#{address}

WHERE M_ID=#{id}

UPDATE MEMBER
SET STATUS='N'
WHERE M_ID=#{id}
