mapper.select:
SELECT p.purchase_no, a.art_title,at.date_time, p.purchase_count,p.art_no,a.address,p.seat_name,p.type, i.change_name,a.art_type, p.seat_sal ,(SELECT R_NO FROM REPLY C JOIN MEMBER M on(C.RWRITER=M.M_ID) where m.M_ID= #{id} AND C.STATUS='Y') as COMMENT_NO, (SELECT rcontent FROM REPLY C JOIN MEMBER M on(C.RWRITER=M.M_ID) where m.M_ID= #{id} AND C.STATUS='Y') as rcontent FROM PURCHASE P JOIN ART a on(p.ART_NO=a.ART_NO) JOIN ART_TIME at on(p.ART_NO=at.ART_NO) JOIN IMG i on(p.ART_NO=i.ART_NO) JOIN MEMBER m on(m.M_NO=p.M_NO) WHERE TO_CHAR(DATE_TIME,'YYYYMMDDHH24MISS') < TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') AND m.M_ID=#{id}
AND ART_TITLE LIKE '%' || #{pName} || '%'
AND ART_TYPE = #{artType}
WHERE TO_CHAR(DATE_TIME,'YYYY-MM-DD') BETWEEN TO_CHAR(#{sd},'YYYY-MM-DD') AND TO_CHAR(#{ed},'YYYY-MM-DD')
